import typer
import py4lexis.cli.session as session
import py4lexis.cli.airflow as airflow
import py4lexis.cli.datasets as datasets
import py4lexis.cli.lexis_irods as irods

app = typer.Typer(name="cli",
                  rich_markup_mode="rich")

app.add_typer(typer_instance=session.app)
app.add_typer(typer_instance=airflow.app)
app.add_typer(typer_instance=datasets.app)
app.add_typer(typer_instance=irods.app)


if __name__ == "__main__":
    app()