from enum import Enum


class Access(str, Enum):
    public="public"
    project="project"
    user="user"


class ActionType(str, Enum):
    stage="stage"
    upload="upload"
    download="download"
    delete="delete"
    

class UploadStatus(str, Enum):
    pending="PENDING"
    success="SUCCESS"


class FilenameOperators(str, Enum):
    eq="eq"
    in_name="in"


class SizeOperators(str, Enum):
    eq="eq"
    l="lt"
    leq="leq"
    g="gt"
    geq="geq"


class ItemType(str, Enum):
    file="file"
    directory="directory"
    root="root"


class Status(str, Enum):
    success="success"
    failed="failed"
    running="running"
    queued="queued"