from logging import Logger
from sys import exit
from datetime import datetime


def get_time(now: datetime) -> float:
    """
        Return time in seconds. 

        Returns
        -------
        float
            Time in seconds.
    """

    elapsed: float = now.timestamp() - datetime(2024, 1, 1, 0, 0).timestamp()
    return elapsed


def log_and_print_errors(logging: Logger, message: str, suppress_print: bool=True, exit_on_error: bool=False):
    logging.error(message)
    if suppress_print:
        print(f"Some errors occurred. See log file, please.\nExiting the script...")
    else:
        print(message)
        print(f"Error has been also logged to log file with further information.\nExiting the script...")
    
    if exit_on_error:
        exit()