class Py4LexisException(Exception):
    """
        Base Py4Lexis exception.
    """
    def __init__(self, message: str):   
        self.message = f"{message} -- FAILED"                 
        super().__init__(self.message)


class Py4LexisAuthException(Py4LexisException):
    """
        Exception representing problems with authentication/authorization (keycloak, irods, etc.).
    """
    def __init__(self, message: str):            
        self.message = f"AUTH -- {message}"
        super().__init__(self.message)
        


class Py4LexisSessionException(Py4LexisException):
    """
        Exception representing problems inside Session class (LexisSession, iRODS, etc.).
    """
    def __init__(self, message: str):            
        self.message = f"SESSION -- {message}" 
        super().__init__(self.message)


class Py4LexisAPIException(Py4LexisException):
    """
        Exception representing problems while using requests methods in LexisSession class.
    """
    def __init__(self, message: str, method: str, url: str):            
        self.message = f"API -- {method} -- {url} -- {message}"
        super().__init__(self.message)


class Py4LexisDownloadException(Py4LexisException):
    """
        Exception representing problems while downloading datasets.
    """
    def __init__(self, message: str, url: str | None = None):
        if url is not None:            
            self.message = f"DOWNLOAD -- {url} -- {message}"
        else:
            self.message = f"DOWNLOAD -- {message}"

        super().__init__(self.message)


